import groovy.xml.MarkupBuilder
import org.custommonkey.xmlunit.*
import java.util.Random  
import java.security.MessageDigest
import java.nio.file.*

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectPath = groovyUtils.projectPath
def project = testRunner.testCase.testSuite.project
def myTestSuite = testRunner.testCase.testSuite;
def testCase = testRunner.testCase
def testStepUploadDataAfterCheck = testCase.getTestStepByName("sendDocument")
def request= testStepUploadDataAfterCheck.testRequest

// clear existing attachments
for( a in request.attachments ) {
	request.removeAttachment( a )
}

/* Same consignmentId for two requests */
def consignmentId = java.util.UUID.randomUUID()

/* Two different dhs properties */
def memberClass = testRunner.testCase.testSuite.project.getPropertyValue("consumerMemberClass")
def memberCode = testRunner.testCase.testSuite.project.getPropertyValue("consumerMemberCode")
def subsystem = testRunner.testCase.testSuite.project.getPropertyValue("consumerSubsystem")
def subsystem2 = testRunner.testCase.testSuite.project.getPropertyValue("consumerSubsystem2")

def fileNamePath = testRunner.testCase.testSuite.project.getPropertyValue("goodCapsule")
def file = new File(fileNamePath)
if ( file == null) {
   log.error "bad filename"
}   
else 
{
 	FileInputStream attFile = null;
	ByteArrayOutputStream bos = null;
	try{
		/* ----------First request---------- */
		// attach and set properties
		def recipientMemberCode =  testRunner.testCase.testSuite.project.getPropertyValue("aditMemberCode")
		def recipientPersonalIdCode = testRunner.testCase.testSuite.project.getPropertyValue("userCode")

		attFile = new FileInputStream(fileNamePath)
		def attStr = attFile.text;
		attStr = attStr.replaceAll( /(?ms)(<DecSender>.*?<OrganisationCode>.*?<\/OrganisationCode>)/, '<DecSender><OrganisationCode>'+ memberCode + '</OrganisationCode>')
		attStr = attStr.replaceAll( /(?ms)(<DecRecipient>.*?<OrganisationCode>.*?<\/OrganisationCode>)/, '<DecRecipient><OrganisationCode>'+ recipientMemberCode + '</OrganisationCode>')
		attStr = attStr.replaceAll( /(?ms)(<.*?DecRecipient>.*?<.*?PersonalIdCode>).*?(<\/.*?PersonalIdCode>)/, '$1'+ recipientPersonalIdCode + '$2')
		attStr = attStr.replaceAll( /(?ms)(<.*?Recipient>.*?<.*?PersonalIdCode>).*?(<\/.*?PersonalIdCode>)/, '$1'+ recipientPersonalIdCode + '$2')
		log.info attStr
		bos = new ByteArrayOutputStream();
		bos.write(attStr.getBytes("UTF-8"))

		def attachment = request.attachBinaryData(bos.toByteArray(), 'application/octet-stream' )
		attachment.setContentID("doc")

		// Replace in request
		holder = groovyUtils.getXmlHolder("sendDocument#Request")
		holder["//prod:consignmentId"] = consignmentId
		holder["//ns3:memberClass"] = memberClass
		holder["//ns3:memberCode"] = memberCode
		holder["//ns3:subsystemCode"] = subsystem
		// write updated request back to teststep 
		holder.updateProperty()
		context.requestContent = holder.xml

		//Run request
		testRunner.runTestStepByName("sendDocument") 
		response = testRunner.testCase.getTestStepByName("sendDocument").testStep.getPropertyValue("response");
		log.info response


		/* ----------Second request---------- */		
		// Replace in request
		holder["//ns3:subsystemCode"] = subsystem2
		// write updated request back to teststep 
		holder.updateProperty()
		context.requestContent = holder.xml

		//Run request
		testRunner.runTestStepByName("sendDocument") 
		response = testRunner.testCase.getTestStepByName("sendDocument").testStep.getPropertyValue("response");
		log.info response
	}  catch(Exception ex) {
		log.error(ex.getMessage(), ex);
	} finally  {
	  	if(attFile != null) {
	  		attFile.close();
	  	}
	  	if(bos != null) {
	  		bos.close();
	  	}
	}
   	log.info "file attached succesfully"
}

